import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Button } from './Button';
import './Navbar.css';
import { Link as ScrollLink } from 'react-scroll';

function Navbar() {
    const [click, setClick] = useState(true);
    const [button, setButton] = useState(true);
    const [page, setPage] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if(window.innerWidth <= 960) {
            setButton(false);
        }
        else {
            setButton(true);
        }
    };

    {/*(const mainScreenCheck = () => {
        if () {

        }
        else {

        }
    };*/}

    window.addEventListener('resize', showButton);

    return (
        <>
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
                        sarah collins
                    </Link>
                    <div className='menu-icon' onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                    </div>
                    <ul className={click ? 'nav-menu active' : 'nav-menu'} >
                        <li className='nav-item'>
                            <ScrollLink to='aboutme' smooth={true} className='nav-links' onClick={closeMobileMenu}>
                                ABOUT ME
                            </ScrollLink>
                        </li>
                        <li className='nav-item'>
                            <ScrollLink to='projects' smoother={true} className='nav-links' onClick={closeMobileMenu}>
                                PROJECTS
                            </ScrollLink>
                        </li>
                        <li className='nav-item'>
                            <ScrollLink to='skills' smooth={true} className='nav-links' onClick={closeMobileMenu}>
                                SKILLS
                            </ScrollLink>
                        </li>
                        <li className='nav-item'>
                            <ScrollLink to='coursework' smooth={true} className='nav-links' onClick={closeMobileMenu}>
                                COURSEWORK
                            </ScrollLink>
                        </li>
                    </ul>
                </div>
            </nav>
        </>
    )
}

export default Navbar
