import React from 'react'
import './BarChart.css'

function BarGroup(props) {
  let barPadding = 2
  let barColour = '#348AA7'
  let widthScale = d => d * (window.innerWidth * .625);

  let width = widthScale(props.d.value)
  let yMid = props.barHeight * 0.5
  
  return <g className="bar-group">
    <text className="name-label" x="-6" y={yMid} alignmentBaseline="middle" >{props.d.name}</text>
    <rect y={barPadding * 0.5} width={width} height={props.barHeight - barPadding} fill={barColour} />
  </g>
}

function Axis(props) {
  return <g>
        <g>
          <text>Beginner</text>
          <rect x={.322 * (window.innerWidth * .625)} y='0' width='2' height={68 + (28 * this.props.barNumber)} fill='#000'></rect>
          <rect x={.644 * (window.innerWidth * .625)} y='0' width='2' height={68 + (28 * this.props.barNumber)} fill='#000'></rect>
          <rect x={.966 * (window.innerWidth * .625)} y='0' width='2' height={68 + (28 * this.props.barNumber)} fill='#000'></rect>
          <rect x={1.288 * (window.innerWidth * .625)} y='0' width='2' height={68 + (28 * this.props.barNumber)} fill='#000'></rect>
          <rect x={1.61 * (window.innerWidth * .625)} y='0' width='2' height={68 + (28 * this.props.barNumber)} fill='#000'></rect>
        </g>
  </g>
}

class BarChart extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: props.barData
        };
    }

  render() {
    let barHeight = 30

    let barGroups = this.state.data.map((d, i) => <g transform={`translate(0, ${i * barHeight})`}>
                                                    <BarGroup d={d} barHeight={barHeight} />
                                                  </g>)        
                                                  
    let axis = <Axis></Axis>
    
    return <svg className="container" >
      <g>
        {<h1 className="title" x="10" y="30">Programming and Scripting Languages</h1>}
        <g className="chart" transform="translate(100,60)">
          {barGroups}
        </g>
      </g>
    </svg>
  }
}

export default BarChart
