import React from 'react'
import styles from './HeroSection.css'
import { Button } from './Button'
import { Link } from 'react-router-dom';
import './ProjectSection.css'
import './Button.css'
import { Link as ScrollLink } from 'react-scroll';

function AboutMeButton(props) {
  const isAboutMeSection = props.isAboutMeSection;
  if (isAboutMeSection) {
    return (
      <Button className='btn btn--medium'>
        <ScrollLink to='footer' smooth={true} offset={-50}>
        Contact Me
        </ScrollLink>
      </Button>
    )
  }
  else {
    return (
      <>
      </>
    )
  }
}

function HeroSection({
  exactClassName,
  rowClass,
  flexDirection,
  textWrapper,
  topLineClass,
  topLine,
  lightText,
  lightTextDesc,
  headlineClass,
  headline,
  description,
  descriptionClass,
  displayButtonBool,
  imgWrapper,
  img,
  heroimg,
  alt,
  imgStart
}
) {
  return (
      <div
        className={exactClassName}
      >
        <div className='container'>
          <div
            className={rowClass}
            style={{
              display: 'flex',
              flexDirection: {flexDirection}
            }}
          >
            <div className='col'>
              <div className={textWrapper}>
                <div className={topLineClass}>{topLine}</div>
                <h1 className={headlineClass}>
                  {headline}
                </h1>
                <p className={descriptionClass}>
                  {description}
                </p>
                <AboutMeButton isAboutMeSection={displayButtonBool} />
              </div>
            </div>
            <div className='col'>
              <div className={imgWrapper}> {/*kinda fucked up but it might fix itself ?? */}
              {/*<div className='home__hero-img-wrapper home__hero-img-wrapper-alt'>*/}
                <img src={img} alt={alt} className={imgClass} />
              </div>
            </div>
          </div>
        </div>
      </div>
  );
}

export default HeroSection;
