import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Button } from './Button';
import './Navbar.css';
import { Link as ScrollLink } from 'react-scroll';

function Navbar() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);
    const goToSection = () => {
        closeMobileMenu();
        const element = document.getElementById('projects');

       if(document.location.pathname !== '/'){
           document.location.pathname = '/';
           setTimeout(3000);
           element = document.getElementById('projects');
           element.scrollIntoView({behavior: 'smooth'});
       }
       else {
            element.scrollIntoView({behavior: 'smooth'});
       }
    }

    const showButton = () => {
        if(window.innerWidth <= 960) {
            setButton(false);
        }
        else {
            setButton(true);
        }
    };

    window.addEventListener('resize', showButton);

    return (
        <>
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
                        sarah collins
                    </Link>
                    <div className='menu-icon' onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                    </div>
                    <ul className={click ? 'nav-menu active' : 'nav-menu'} >
                        <li className='nav-item'>
                            <div className='nav-links' onClick={goToSection}>
                                Hello
                            </div>
                        </li>
                        <li className='nav-item'>
                            <div className='nav-links' onClick={goToSection}>
                                Projects
                            </div>
                        </li>
                        {/*<li className='nav-item'>
                            <ScrollLink to='aboutme' smooth={true} onClick={closeMobileMenu}>
                                <Link to='/' className='nav-links'>
                                    ABOUT ME
                                </Link>
                            </ScrollLink>
    </li>*/}
                        <li className='nav-item'>
                            <ScrollLink to='projects' smooth={true} className='nav-links' onClick={closeMobileMenu}>
                                <Link to='/'>
                                    PROJECTS
                                </Link>
                            </ScrollLink>
                        </li>
                        <li className='nav-item'>
                            <ScrollLink to='skills' smooth={true} className='nav-links' onClick={closeMobileMenu}>
                                SKILLS
                            </ScrollLink>
                        </li>
                        <li className='nav-item'>
                            <ScrollLink to='coursework' smooth={true} className='nav-links' onClick={closeMobileMenu}>
                                COURSEWORK
                            </ScrollLink>
                        </li>
                    </ul>
                </div>
            </nav>
        </>
    )
}

export default Navbar
