import React, { PureComponent } from 'react';
import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const data = [
  {
    name: 'C', value: 1
  },
  {
    name: 'Page B', value: 4
  },
  {
    name: 'Page C', value: 5
  },
  {
    name: 'Page D', value: 3
  },
  {
    name: 'Page E', value: 3
  }
];

const renderCustomAxisTick = ({x, y, payload}) => {
  let title = '';
  let xspacing = 0;
  let yspacing = 0;

  if (payload.value == '1')
  {
    title = 'Beginner';
    xspacing = 25;
    if (x < 500)
    {
      yspacing = 40;
    }
  }
  else if (payload.value == '2')
  {
    title = 'Elementary';
    xspacing = 35;
  }
  else if (payload.value == '3')
  {
    title = 'Intermediate';
    xspacing = 35;
  }
  else if (payload.value == '4')
  {
    title = 'Advanced';
    xspacing = 25;
  }
  else if (payload.value == '5')
  {
    title = 'Expert';
    xspacing = 20;
  }
  
  return (
    <text x={x - xspacing} y={y - yspacing} fontSize="14" viewBox="0 0 1024 1024" fill="#666">
       {title}
    </text>
  )
};

class Example extends PureComponent {
  render() {
    return (
      <div className="chartcontainer"> 
        <ResponsiveContainer width={500} height={500}>
          <BarChart
            width={500}
            height={500}
            data={data}
            layout="vertical"
            margin={{
              top: 5,
              right: 30,
              left: 20,
              bottom: 5,
            }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis type="number" tickCount={6} domain={[0,5]} tick={renderCustomAxisTick} orientation="top"/>
            <YAxis type="category" dataKey="name"/> 
            <Tooltip />
            <Bar dataKey="value" fill="#8884d8"/>
          </BarChart>
        </ResponsiveContainer>
      </div>
    );
  }
}

export default Example
