import React, { PureComponent } from 'react';
import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const data = [
  {
    name: 'Page A', value: 1
  },
  {
    name: 'Page B', value: 4
  },
  {
    name: 'Page C', value: 5
  },
  {
    name: 'Page D', value: 3
  },
  {
    name: 'Page E', value: 3
  }
];

const renderCustomAxisTick = ({x, y, payload}) => {
  let title = '';

  switch (payload.value) {
    case '1':
      title = 'Beginner';
      payload.value = 'Beginner';
      break;
    case '2':
      title = 'Elementary';
      payload.value = 'Elementary';
      break;
    case '3':
      title = 'Intermediate';
      break;
    case '4':
      title = 'Advanced';
      break;
    case '5':
      title = 'Expert';
      break;
    default:
      title = '';
      break;
  }

  return (
    <text x={x - 20} y={y + 10} fontSize="12" viewBox="0 0 1024 1024" fill="#666">
      {payload.value}
    </text>
  )
};

class Example extends PureComponent {
  render() {
    return (
      <div> 
        <BarChart
          width={500}
          height={500}
          data={data}
          layout="vertical"
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis type="number" tickCount={6} domain={[0,5]} tick={renderCustomAxisTick}/>
          <YAxis type="category" dataKey="name"/> 
          <Tooltip />
          <Bar dataKey="value" fill="#8884d8"/>
        </BarChart>
      </div>
    );
  }
}

export default Example
